const today = new Date();

const payload = msg.payload
    .reduce((acc, el) => {
        return acc.concat(el)
    }, [])
    .filter(s => {
        const d = new Date(s.last_updated)
        return sameDay(d, today) && 
            d.getHours() > 4 &&
            (s.state == 'open' || s.state == 'closed')
    })

const newMsg = Object.assign({}, msg, { payload });

if (payload.length > 0) {
    node.status({fill:"green",shape:"dot",text:"has opened"});
    return [newMsg, null];
} else {
    node.status({fill:"red",shape:"dot",text:"not opened"});
    return [null, newMsg];
}


function sameDay(d1, d2) {
  return d1.getFullYear() === d2.getFullYear() &&
    d1.getMonth() === d2.getMonth() &&
    d1.getDate() === d2.getDate();
}

const photos = msg.payload.map(result => ({
    file: result.data.filename
}))

return {
    payload: {
        data: {
            message: "Camera snapshots:",
            data: {
                photo: photos,
            },
        }
    }
}

const newPayload = msg.payload.map(entity_id => ({ 
    data: {
        entity_id: `camera.${entity_id}`,
        filename: `/tmp/homekit/${entity_id}.jpeg`,
    }
}));

return {
    payload: newPayload,
}
