const ENTITY_NAMES = {
    'device_tracker.nicksiphone': 'Nick',
    'device_tracker.charlotte_phone': 'Charlotte',
    'device_tracker.joesiphone': 'Joe',
    'device_tracker.katesiphone_2': 'Kate',
    'device_tracker.louises7iphone': 'Louise',
    'device_tracker.oliversiphone': 'Oliver',
    'device_tracker.peter_phone': 'Peter',
}

const states = msg.payload
    .reduce((states, acc) => acc.concat(states), [])
    .filter(s => s.state === 'not_home')
    .filter(s => within(s.last_updated, 60 * 10))
    .sort((s1, s2) => cmpDates(s1.last_updated, s2.last_updated))
    .map(s => ENTITY_NAMES[s.entity_id])
    .filter(s => !!s);

return {
    payload: states
}

function cmpDates(d1, d2) {
    return new Date(d1) - new Date(d2);
}

function within(d, seconds) {
    return new Date(d) > new Date(new Date().getTime() - seconds * 1000);
}

function getPeople() {
    if (msg.payload.length === 0) {
        return "Someone";
    }

    if (msg.payload.length === 1) {
        return msg.payload[0];
    }

    const [last, ...people] = msg.payload;
    return `${people.join(', ')} and ${last}`;
}

const people = getPeople();

return {
    payload: {
        data: {
            message: `${people} just left home, so I will arm the alarm.`,
        }
    }
}

